Vue.component("v-nus",{template:"<div :id=\"'vue-nouislider-' + id\"></div>",props:{id:{type:String,default:function(){return Math.random().toString(36).substr(2,4)}},config:Object,value:Array,log:Boolean},data:function(){return{init:!1}},computed:{slider:function(){return document.getElementById("vue-nouislider-"+this.id)}},mounted:function(){var i=this;i.config.start=i.value,noUiSlider.create(i.slider,i.config),i.slider.noUiSlider.on("update",(function(t,e){i.$emit("update",t),i.log&&window.console.log("[vnus]<"+e+">"+t)}))},watch:{value:function(i){if(this.init){var t=this.slider.noUiSlider.get();Array.isArray(t)||(t=[t]),JSON.stringify(t)!==JSON.stringify(i)&&this.slider.noUiSlider.set(i)}else this.init=!0}}});